// NAVBAR

import React from 'react';
import Auth from '../../utils/auth';
import { Link } from "react-router-dom";
import icon from '../../assets/icon.png'


//Scroll Feature
function Nav({ setCategory }) {
    const [state, dispatch] = useStoreContext();
    const { categories } = state;
    const { loading, data: categoryData } = useQuery(QUERY_CATEGORIES);

    window.onscroll = function(){
        scrollFn()
    }
    function scrollFn() {
        if(document.body.scrollTop > 50 || document.documentElement.scrollTop > 75) {
            document.getElementById("navBar").setAttribute("class", "endState btn-grad")
        }else {
            document.getElementById("navBar").setAttribute("class", "initialState")
        }
    }



    function showNavigation() {
        if (Auth.loggedIn()) {
            return (
                <ul className="flex-row">
                    <li className="mx-1">
                        <Link to="/orderHistory">
                            Order History
                        </Link>
                    </li>
                    <li className="mx-1">
                        <a href="/" onClick={() => Auth.logout()}>
                            Logout
                        </a>
                    </li>
                </ul>
            );
        } else {
            return (
                <ul className="flex-row">
                    <li className="mx-1">
                        <Link to="/signup">
                            Signup
                        </Link>
                    </li>
                    <li className="mx-1">
                        <Link to="/login">
                            Login
                        </Link>
                    </li>
                </ul>
            );
        }
    }

    return (
        <header className="flex-row px-1 navHeader">
            <h1>
                <Link to="/">
                    <img src={icon} id="icon"></img>
                    2020-2021 WebDev Gifts
                </Link>
            </h1>

            <div id="navBar" className="navBarChoices">
                {showNavigation()}
            </div>
        </header>
    );
}


export default Nav;
