//  DETAIL

import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useQuery } from '@apollo/react-hooks';
import { QUERY_PRODUCTS } from '../utils/queries';
import spinner from '../assets/spinner.gif'
import { useStoreContext } from '../utils/GlobalState';
import Cart from '../components/Cart';
import { idbPromise } from '../utils/helpers';
// import 'boostrap/dist/css/bootstrap.min/css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import {
    REMOVE_FROM_CART,
    UPDATE_CART_QUANTITY,
    ADD_TO_CART,
    UPDATE_PRODUCTS
} from '../utils/actions';

function Detail() {
    const [state, dispatch] = useStoreContext();
    const { id } = useParams();
    const [currentProduct, setCurrentProduct] = useState({});
    const { loading, data } = useQuery(QUERY_PRODUCTS);
    const { products, cart } = state;

    const [value, setValue] = useState('');
    const handleSelect = (e) => {
        console.log(e);
        setValue(e);

    const addToCart = () => {
        const itemInCart = cart.find((cartItem) => cartItem._id === id);

        if (itemInCart) {
            dispatch({
                type: UPDATE_CART_QUANTITY,
                _id: id,
                purchaseQuantity: parseInt(itemInCart.purchaseQuantity) + 1
            });

            idbPromise('cart', 'put', {
                ...itemInCart,
                purchaseQuantity: parseInt(itemInCart.purchaseQuantity) + 1
            })
        } else {
            dispatch({
                type: ADD_TO_CART,
                product: { ...currentProduct, purchaseQuantity: 1 }
            });
            idbPromise('cart', 'put', { ...currentProduct, purchaseQuantity: 1 });
        };
    };

    const removeFromCart = () => {
        dispatch({
            type: REMOVE_FROM_CART,
            _id: currentProduct._id
        });
        idbPromise('cart', 'delete', { ...currentProduct });
    };
    useEffect(() => {
        // products already in globalStore
        if (products.length) {
            setCurrentProduct(products.find(product => product._id === id));

            // retrieved from server
        } else if (data) {
            dispatch({
                type: UPDATE_PRODUCTS,
                products: data.products
            });

            data.products.forEach(product => {
                idbPromise('products', 'put', product);
            });


            // get cache from idb
        } else if (!loading) {
            idbPromise('products', 'get').then((products) => {
                dispatch({
                    type: UPDATE_PRODUCTS,
                    products: products
                });
            });
        }
    }, [products, data, loading, dispatch, id]);



    };


    return (
        <>
            {currentProduct ? (
                <div className="container">
                    <Link to="/">
                        ← Back to Products
                </Link>
                    <div className="detail-row">
                        <h1>{currentProduct.name}</h1>
                    </div>
                    <div className="detail-row">
                        <p>{currentProduct.description}</p>
                    </div>
                    <div className="detail-row">

                        <img src={`/images/${currentProduct.image}`} alt={currentProduct.name} id="detailImage" />
                    </div>
                    {/* <select class="form-select" aria-label="Default select example">
                        <option selected>Select a size</option>
                        <option value="Small">Small</option>
                        <option value="Medium">Medium</option>
                        <option value="Large">Large</option>
                        <option value="XL">XL</option>
                    </select> */}

                    <DropdownButton
                        alignRight
                        title="Dropdown right"
                        id="dropdown-menu-align-right"
                        onSelect={handleSelect}>
                        <Dropdown.Item eventKey="Small">Small</Dropdown.Item>
                        <Dropdown.Item eventKey="Medium">Medium</Dropdown.Item>
                        <Dropdown.Item eventKey="Large">Large</Dropdown.Item>
                        <Dropdown.Item eventKey="XL">XL</Dropdown.Item>
                    </DropdownButton>
                    <h4>you selected {value}</h4>

                    <div className="detail-row"><h2>Price: ${currentProduct.price}{" "}</h2>
                        <button onClick={addToCart}>
                            Add To Cart
                        </button>
                    </div>
                </div>

            ) : null}
            {
                loading ? <img src={spinner} alt="loading spinner" /> : null
            }
            <Cart />
        </>
    );
};
export default Detail;
