// NAVBAR

import React from 'react';
import Auth from '../../utils/auth';
import { Link } from "react-router-dom";
import icon from '../../assets/icon.png'



function Nav() {
    window.onscroll = function(){
        scrollFn()
    }

    function scrollFn() {
        if(document.body.scrollTop > 50 || document.documentElement.scrollTop > 75) {
            // document.getElementById("hero").style.backgroundColor = "#005000";
            // document.getElementById("hero").style.padding = "25px 10px";
            // document.getElementById("hero").style.top = "5px";
            // document.getElementById("hero").style.left = "90%";
            // document.getElementById("hero").style.borderRadius = "50%";
            // document.getElementById("hero").style.width = "10";

            document.getElementById("navBar").setAttribute("class", "endState btn-grad")
        }else {
            document.getElementById("navBar").setAttribute("class", "initialState")
        }
    }



    function showNavigation() {
        if (Auth.loggedIn()) {
            return (
                <ul className="flex-row">
                    <li className="mx-1">
                        <Link to="/orderHistory">
                            Order History
                        </Link>
                    </li>
                    <li className="mx-1">
                        <a href="/" onClick={() => Auth.logout()}>
                            Logout
                        </a>
                    </li>
                </ul>
            );
        } else {
            return (
                <ul className="flex-row">
                    <li className="mx-1">
                        <Link to="/signup">
                            Signup
                        </Link>
                    </li>
                    <li className="mx-1">
                        <Link to="/login">
                            Login
                        </Link>
                    </li>
                </ul>
            );
        }
    }

    return (
        <header className="flex-row px-1 navHeader">
            <h1>
                <Link to="/">
                    <img src={icon} id="icon"></img>
                    2020-2021 WebDev Gifts
                </Link>
            </h1>

            <button id="navBar" className="navBarChoices">
                {showNavigation()}
            </button>
        </header>
    );
}


export default Nav;
